import { createPortal } from "react-dom";
import { FileText, HelpCircle, Home, Layers, PieChart, Search, Settings } from "lucide-react";
import { useNavigate } from "react-router-dom";

const Sidebar = () => {
  const navigate = useNavigate();

  const menuItems = [
    { icon: <Home size={20} />, label: "Dashboard", onClick: () => navigate("/") },
    { icon: <PieChart size={20} />, label: "Analytics" },
    { icon: <FileText size={20} />, label: "Reports" },
    { icon: <Search size={20} />, label: "Search" },
    { icon: <Settings size={20} />, label: "Settings" },
    { icon: <HelpCircle size={20} />, label: "Help" },
    { icon: <Layers size={20} />, label: "Master", onClick: () => navigate("/master") }
  ];

  return (
    <div className="fixed pt-3 max-w-[60px] w-full flex flex-col bg-[#020234] min-h-screen gap-10">
      {menuItems.map((item, index) => (
        <div
          key={index}
          className="group relative flex flex-col items-center cursor-pointer text-white"
          onClick={item.onClick}
        >
          {item?.icon}

          {createPortal(
            <span className="fixed font-semibold left-[70px] top-auto bg-white text-blue-800 text-lg px-2 py-1 rounded opacity-0 group-hover:opacity-100 transition-all whitespace-nowrap shadow-md z-[9999]">
              {item?.label}
            </span>,
            document.body
          )}
        </div>
      ))}
    </div>
  );
};

export default Sidebar;
